//# sourceMappingURL=jquery.nanoscroller.min.js.map
$(".main-menu__content").nanoScroller();
$('.sub-toggle-ext').click(function(){
	setTimeout(function(){
		$(".main-menu__content").nanoScroller();
	}, 200);
});

function mainMenuElemRemove() {
		$('.sub-toggle-ext').each(function(){
			$(this).parent('li').removeClass('open');
			$(this).parent('li').find('> ul').hide('fast');
		})
		$('.sub-toggle').each(function(){
			$(this).parent('li').removeClass('open');
			$(this).parent('li').find('> ul').hide('fast');
		})
}

(function($){


	$('.yay-toggle').click(function(){
		$('body').toggleClass('menu-hide');
		mainMenuElemRemove();
	})

	$('.app-switcher').on('show.bs.dropdown', function(){
		$('.main-menu').addClass('main-menu__toggled');
	});

	$('.app-switcher').on('hide.bs.dropdown', function(){
		$('.main-menu').removeClass('main-menu__toggled');
	});

	$(document).on('click', '.sub-toggle-ext, li .yay-collapse-icon', function(e){
		e.preventDefault();
		var obj = $(this);
		if (!obj.parent('li').hasClass('open')) {
			obj.parent('li').addClass('open');
			obj.parent('li').find('> ul').show('fast');
		} else {
			obj.parent('li').removeClass('open');
			obj.parent('li').find('.open').removeClass('open');
			obj.parent('li').find('ul').hide('fast');
		}
	})

})(jQuery);
$(window).on('load resize', function(){
	if ($(window).width() <= 1024) {
		$('body').addClass('menu-hide');
		mainMenuElemRemove();
	} else {
		$('body').removeClass('menu-hide');
	}
})